css:
.product__payment>a{
  display: none !important;
}
.top-bar__nav{
  display: none !important;
}
.app__header{
    background: #fff !important;
}

.header__nav a:last-child {
    background: transparent;
    color: #e8ccc8;
    border-radius: 0;
}
.product-card__pictures{
  height: 385px !important;
}

@media (min-width: 576px){
  .product-card__picture img {
    max-height: 350px;
  }
 }  

@media(max-width: 450px){
  .product-card__pictures {
    height: 177px !important;
  }}

.footer__title{
  text-transform: uppercase;
  font-weight: 500;
  color: #ffb9be !important;
}
.footer a{
  color: black;
}
.footer
.prices__discount span {
    color: #3c9430;
}
.product__prices .prices__value:before {
    content: "" !important;
}

.prices--big .prices__value {
    font-size: 27px !important;
    color: #3c9430 !important;
}

.product-card__prices .prices__value {
    color: #4ba02d;
    font-size: 21px !important;
    font-weight: bold !important;
}

.prices__discount:before {
    content: "ou";
}

.prices--big .prices__value {
    font-size: 27px !important;
    color: #4ba02d;
    font-weight: 600 !important;
}
.product-card__prices .prices__value:first-child {
    /* margin-top: calc(var(--line-height)*var(--font-size)); */
    margin-top: -8px !important;
}
@media (max-width: 450px){
  .top-bar__countdown{
    font-weight:800 !important;
  }
.header__logo {
    max-width: 145px;
    max-height: 60px;
  }}

.header__search-btn:after {
    font-size: 20px;
    content: "OK";
    font-weight: 500;
    color: #454a47;
    text-transform: lowercase;
    text-decoration: underline;
}
.header__nav a {
    font-weight: 700;
    color: #e8ccc8;
    padding: 7px;
    border-radius: 0px !important;
}
.header__nav a:hover {
    background: var(--primary-lightest);
    text-decoration: none;
    background: #e8ccc8;
    color: white;
    font-weight: 600;
}
.menu__item:hover {
    text-decoration: none;
    background: var(--primary-lightest);
    background: #e8ccc8;
    color: white;
    font-weight: 700;
}
.search__input {
    padding-right: var(--spacer-5);
    border-radius: 0px;
}
.search-engine__info {
    padding-bottom: var(--spacer-3);
    text-transform: uppercase;
    color: #9e0017 !important;
    text-decoration: underline;
}
.btn-primary {
    color: #fff;
    background-color: #4ba02d;
    border-color: #4ba02d;
    border-radius: 0px !important;
    border: none;
}
.product__discount {
  display: none !important;
}
.variations__grid {
    margin-bottom: var(--spacer-3);
    text-transform: uppercase;
    margin-top: 30px;
}
.product-card__offer-stamp{
  display:none !important;
}
.product__name{
  font-size: 1.2em !important;
}
.prices--big .prices__value{
  font-size: 30px !important;

}
.h5, h5{
  font-size: 16px;
}
.product__buy .btn-primary{
  width: 100% !important;
  text-transform: uppercase !important;
}
.prices__value{
  font-size: 16px !important;
  font-weight: 600 !important;
}
.btn-primary.focus, .btn-primary:focus, .btn-primary:hover {
    color: #fff;
    background-color: #29e466;
    /* border-color: #1a1a1a; */
}
.prices--big .prices__value {
    font-size: 25px !important;
    color: #29e466;
}
.product-card__name {
    font-size: 15px !important;
    padding-bottom: 0px !important;
    text-transform: uppercase;
}
.banners-grid{
  text-align : center !important;
}
.product-card__buy .btn {
  display: none !important;
}

.product__short-stock{
  display:none !important;
}

footer .header__logo {
    margin-bottom: .5rem;
    width: auto;
    height: 50px;
}
.footer {
    background: #f1f1f1 !important;
}
.order-lg-first p {
  font-size: 12px !important;
}

@media (min-width: 992px){
.top-bar__countdown {
    font-size: var(---font-size-lg);
    padding-top: var(--spacer-2);
    padding-bottom: var(--spacer-2);
    font-weight: 800;

  } 
 }
.product__sku{
  display: none !important;
}
.products-carousel__title {
    text-transform: uppercase;
    color: #e8ccc8;
    font-weight: 500;
}
.glide__bullet{
  display: none !important;
}

.minicart__btn-checkout, .cart__btn-checkout{
  text-transform: uppercase;
    font-weight: 700;
    background-color: #37a214;
}
.product-card__prices .prices__installments:before {
    content: "";
}

.product-card__prices .prices__value:before{
  display: none !important;
}
.minicart__btn-cart{
  background: #c1c1c1;
    color: black;
    text-transform: uppercase;
}
.cart-item__name{
  text-transform: uppercase;
}
@media (min-width: 992px) {
.product-card__picture img {
      max-height: 400px;
}
.fa-search{
  display:none !important;
}
  .shipping-calculator__free-from-value .progress-bar {
    background-color: #45040a;
    text-transform: uppercase;
}
.login__oauth{
  display: none !important;
  }
}
.product__free-shipping-from{
  display: none !important;
}
.minicart__subtotal{
  color : #4d4949;
}
.prices.product-card__prices, .product-card__name{
  padding-bottom: 15px;
}
